package Stringc;

public class Startend {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		String sd ="this is new";
		if(sd.startsWith("thiss"))
			System.out.println("the word matches");
		else
			System.out.println("the word doesnt matches");
		
		if(sd.endsWith("news"))
			System.out.println("2nd string match");
		else
			System.out.println("string doesnt match");
	}

}
