package Stringc;

public class Substing {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		String st="thisisticket".substring(3, 6);
		String s="this".substring(2);
		String q="there is the bat".substring(5);
		CharSequence c="this is the object".subSequence(3, 4);
		//System.out.println("the string is",+st);
		System.out.println("this value is:"+s);
		System.out.println("the value is:"+q);
		System.out.println("the value is:"+c);
		System.out.println("the valueis:"+st);
		
		
	}

}
